mapper.select:
		select
    		*
		from nideshop_stroe
		WHERE 1=1

			AND Longitude > #{minLng}


			AND Longitude <  #{maxLng}


			AND Latitude > #{minLat}


			AND Latitude < #{maxLat}


			AND store_type=#{storeType}


			AND id=#{stroeid}


		    AND store_name LIKE concat('%',#{name},'%')


            AND deliveryfee=#{deliveryfee} order by deliveryfee asc


            AND sendingfee=#{sendingfee} order by sendingfee asc



                order by ${sidx} ${order}


                order by id asc



			limit #{offset}, #{limit}

select
*
from nideshop_stroe
WHERE 1=1

    AND Longitude > #{minLng}


    AND Longitude <  #{maxLng}


    AND Latitude > #{minLat}


    AND Latitude < #{maxLat}


    AND store_type=#{storeType}



        order by ${sidx} ${order}


        order by id desc



    limit #{offset}, #{limit}

mapper.update:
update nideshop_stroe
`store_name` = #{storeName}, `store_type` = #{storeType}, `store_picrue` = #{storePicrue}, `country` = #{country}, `province` = #{province}, `city` = #{city}, `distrct` = #{distrct}, `address` = #{address}, `departmentid` = #{departmentid}, `supplierid` = #{supplierid}, stroe_phone=#{storePhone}, stroe_grade=#{stroeGrade}, sales_volume=#{number}
where id = #{id}
